var show={info:{top:0,left:0,zIndex:500,number:70},down:function(){var f=window.innerWidth;var g=window.innerHeight;var c=document.createElement("canvas");c.style.position="fixed";c.style.pointerEvents="none";c.style.top=show.info.top+"px";c.style.left=show.info.left+"px";c.style.zIndex=show.info.zIndex;c.width=f;c.height=g;document.body.appendChild(c);var e=[];for(var a=0;a<show.info.number;a++){e.push({x:Math.random()*f,y:Math.random()*g,r:Math.random()*4+1,n:Math.random()*70})}var b=c.getContext("2d");var d=0;setInterval(function(){b.clearRect(0,0,f,g);b.fillStyle="rgba(255, 255, 255, 0.6)";b.shadowBlur=5;b.shadowColor="rgba(255, 255, 255, 0.9)";b.beginPath();for(var h=0;h<70;h++){var j=e[h];b.moveTo(j.x,j.y);b.arc(j.x,j.y,j.r,0,Math.PI*2,0)}b.fill();d+=0.01;for(var h=0;h<70;h++){var j=e[h];j.y+=Math.cos(d+j.n)+1+j.r/2;j.x+=Math.sin(d)*2;if(j.x>f+5||j.x<-5||j.y>g){e[h]=h%3>0?{x:Math.random()*f,y:-10,r:j.r,n:j.n}:Math.sin(d)>0?{x:-5,y:Math.random()*g,r:j.r,n:j.n}:{x:f+5,y:Math.random()*g,r:j.r,n:j.n}}}},60)}};